# -*- coding: utf-8 -*-
__author__ = 'Yaicky'
import sys

cur, all, demend = 0, 0, 0
stores, prices, product = [], [], []
for line in sys.stdin:
    if cur == 0:
        all, demend = map(int, line.split())
    elif cur == 1:
        for store in line.split():
            stores.append(int(store))
    else:
        for price in line.split():
            prices.append(int(price))
        # print(all, len(stores), len(prices))
        for i in range(min(len(stores), len(prices))):
            if stores[i] == 0:
                continue
            product.append([stores[i], prices[i]])

        product = sorted(product, key=lambda x:x[1]/x[0], reverse=True)
        # print(product)
        max_price = 0
        for n in product:
            if demend<=0:
                break
            if n[0]<=demend:
                demend -= n[0]
                max_price += n[1]
            else:
                max_price += n[1]/n[0]*demend
                demend = 0

        print('%.2f' %(max_price))

    cur += 1
    if cur == all:
        cur = 0
